(function() {
  // a very simple asynchronous js loader
// @author: ktmud
window.Do = window.Do || function(fn) {
  typeof fn == 'function' && setTimeout(fn, 0);
};
Do.add_js = function add_js(src) {
  var x = document.createElement('script');
  x.src = src;
  document.getElementsByTagName('head')[0].appendChild(x);
};
Do.add_css = function add_css(src, cb) {
  var x = document.createElement('link');
  x.rel = 'stylesheet';
  x.href = src;
  document.getElementsByTagName('head')[0].appendChild(x);
};
Do.check_js = function check_js(indicator, callback) {
  var indicator_ret = indicator();
  if (indicator_ret) {
    callback(indicator_ret);
  } else {
    setTimeout(function() {
      check_js(indicator, callback);
    }, 33);
  }
};

Do(function() {

  var add_js = Do.add_js;

  add_js(isay_src);

  var thelabel = $('#isay-label');
  var thetextarea = $('#isay-cont');
  var root = $('#db-isay');

  function check_ready(elem) {
    if (Do.ISay) {
        Do.ISay.init(elem);
    } else {
      //add_js(isay_src);
      setTimeout(function() {
        check_ready(elem);
      }, 80);
    }
    if (!$.fn.tagsug) add_js(tagsug_src);
  }

  function init_isay(e) {
    var elem = e.target;
    var etype = e.type;
    var node = $(elem);

    if (elem.tagName == 'A') {
      var no_action = !node.data('action');
      if (elem.href.split('http')[0] == '' && no_action) {
        return;
      }
      e.preventDefault();
      if (no_action) return;
    }

    if (!Do.ISay) {
      thelabel.text('正在初始化...');
    }

    init_isay = null;

    if (elem.tagName == 'INPUT' || etype === 'dragenter') {
      // for upload file..
      setTimeout(function() {
        check_ready(elem, etype);
      }, 100);
      return;
    }

    thetextarea.focus();

    check_ready(elem);

    e.stopPropagation();
  }

  // to prevent trigger more than one time
  function handleEvent(e) {
    init_isay && init_isay(e);
  }

  setTimeout(function() {
    if (thetextarea.val()) thelabel.hide();
  }, 50);

  $('#isay-upload-inp').one('change', handleEvent);

  thetextarea.one('focus dragenter', handleEvent);

  root.one('click', handleEvent);

  if (location.search) {
    var params = location.search.substring(1).split('&');
    var topic;
    for (var i = 0; i < params.length; i++) {
        var _m = params[i].split('=');
        if (_m[0] === 'topic') {
            topic = decodeURIComponent(_m[1]);
        }
    }

    if (topic) {
        thetextarea.val('#' + topic + '# ');
        var el = thetextarea[0];
        if (el.setSelectionRange) {
           el.focus();
           el.setSelectionRange(el.value.length, el.value.length)
        } else {
           range = el.createTextRange();
           range.collapse(false);
           range.select();
        }
    }
  }

  var actField = $('#isay-act-field'),
      main = actField.siblings('.item'),
      submit = $('#isay-submit'),
      btnGroup = root.find('.btn-group'),
      isayLinks = root.find('.isay-links'),
      activeItem = isayLinks.find('.active'),
      commodityPublishWays = isayLinks.find('.commodity-link .publish-ways');

  function toggleCommodityPublishWays(state) {
      state = state === undefined ? !commodityPublishWays.data('opened') : state;
      commodityPublishWays.toggle(state).data('opened', state);
  }

  toggleCommodityPublishWays(false);

  $(document).click(function(evt) {
    var target = evt.target;
    if (commodityPublishWays.data('opened')) {
      if (target !== commodityPublishWays[0] && !$.contains(commodityPublishWays[0], target)) {
        toggleCommodityPublishWays(false);
      }
    }
  });

  isayLinks.click(function(evt) {
    if (evt.target.nodeName !== 'A') {
      evt.stopImmediatePropagation();
    }
  });

  isayLinks.find('a').click(isayItemHandler);

  actField.delegate('a', 'click', function(evt) {
    if (root.hasClass('share-mode')) {
      isayItemHandler(evt);
    }
  });

  function isayItemHandler(evt) {
    var link = $(evt.target),
        href = link.attr('href'),
        action = link.data('action'),
        item = link.parents('li'),
        isCurrentItem = item.hasClass('active'),
        commodityState;

    if (action === 'commodity') {
      toggleCommodityPublishWays();
      evt.stopImmediatePropagation();
      return;
    } else {
      toggleCommodityPublishWays(false);
    }

    if (isCurrentItem && Do.ISay) {
      switch (action) {
        case 'main':
          thetextarea.focus();
          break;
        case 'share':
          $('#isay-inp-url').focus();
          break;
      }
      evt.stopImmediatePropagation();
      return;
    }

    if (action && !isCurrentItem) {
      activeItem.removeClass('active');
      item.addClass('active');
      activeItem = item;
    }
  }

});

})();

